import { SeedOauthClientCredentialsDefaultClient } from "@fern/oauth-client-credentials-default";

const seedOauthClientCredentialsDefault = new SeedOauthClientCredentialsDefaultClient({ environment: "YOUR_BASE_URL", clientId: "YOUR_CLIENT_ID", clientSecret: "YOUR_CLIENT_SECRET" });
await seedOauthClientCredentialsDefault.auth.getToken({
    clientId: "string",
    clientSecret: "string",
    grantType: "client_credentials"
});
