import { SeedOauthClientCredentialsEnvironmentVariablesClient } from "@fern/oauth-client-credentials-environment-variables";

const seedOauthClientCredentialsEnvironmentVariables = new SeedOauthClientCredentialsEnvironmentVariablesClient({ token: "YOUR_TOKEN" });
await seedOauthClientCredentialsEnvironmentVariables.auth.getTokenWithClientCredentials({
    clientId: "string",
    clientSecret: "string",
    audience: "https://api.example.com",
    grantType: "client_credentials",
    scope: "string"
});

import { SeedOauthClientCredentialsEnvironmentVariablesClient } from "@fern/oauth-client-credentials-environment-variables";

const seedOauthClientCredentialsEnvironmentVariables = new SeedOauthClientCredentialsEnvironmentVariablesClient({ token: "YOUR_TOKEN" });
await seedOauthClientCredentialsEnvironmentVariables.auth.refreshToken({
    clientId: "string",
    clientSecret: "string",
    refreshToken: "string",
    audience: "https://api.example.com",
    grantType: "refresh_token",
    scope: "string"
});
