import { SeedOauthClientCredentialsClient } from "@fern/oauth-client-credentials";

const seedOauthClientCredentials = new SeedOauthClientCredentialsClient({ environment: "YOUR_BASE_URL", clientId: "YOUR_CLIENT_ID", clientSecret: "YOUR_CLIENT_SECRET" });
await seedOauthClientCredentials.auth.getTokenWithClientCredentials({
    clientId: "string",
    clientSecret: "string",
    audience: "https://api.example.com",
    grantType: "client_credentials",
    scope: "string"
});

import { SeedOauthClientCredentialsClient } from "@fern/oauth-client-credentials";

const seedOauthClientCredentials = new SeedOauthClientCredentialsClient({ environment: "YOUR_BASE_URL", clientId: "YOUR_CLIENT_ID", clientSecret: "YOUR_CLIENT_SECRET" });
await seedOauthClientCredentials.auth.refreshToken({
    clientId: "string",
    clientSecret: "string",
    refreshToken: "string",
    audience: "https://api.example.com",
    grantType: "refresh_token",
    scope: "string"
});
